 function shareAnchor(){var e="https://www.facebook.com/sharer/sharer.php?u="+returnUrl();var t="https://twitter.com/intent/tweet?url="+returnUrl()+"&text="+returnTitle();var n="https://plus.google.com/share?url="+returnUrl();var r="https://www.linkedin.com/shareArticle?mini=true&url="+returnUrl()+"&title="+returnTitle();var i="http://www.tumblr.com/share/link?url="+returnUrl()+"&name="+returnTitle();var s='<span id="share-anchor"><a href="'+e+'" target="_blank" class="share-anchor-button share-anchor-facebook default"><i class="fa fa-facebook"></i></a> <a href="'+t+'" target="_blank" class="share-anchor-button share-anchor-twitter default"><i class="fa fa-twitter"></i></a> <a href="'+n+'" target="_blank" class="share-anchor-button share-anchor-google default"><i class="fa fa-google-plus"></i></a> <a href="'+r+'" target="_blank" class="share-anchor-button share-anchor-linkedin default"><i class="fa fa-linkedin"></i></a> <a href="'+i+'" target="_blank" class="share-anchor-button share-anchor-tumblr default"><i class="fa fa-tumblr"></i></a></span>';return s}function returnTitle(){var e=$(document).attr("title");return encode(e)}function returnUrl(e){var t=$(location).attr("href");return encode(t)}function encode(e){return encodeURIComponent(e)}$(document).ready(function(){$(".footnote").append(shareAnchor())})